      let starApi;

      function toggleCollapsible() {
        const collapsible = document.getElementById('aetherCollapsible');
        const header = collapsible.querySelector('.aether-header');
        const content = document.getElementById('aetherContent');
        const contentInner = content.querySelector('.aether-content-inner');
        const collapseButton = content.querySelector('.aether-collapse');
        const toggleIcon = header.querySelector('.toggle-icon');
        const isExpanded = collapsible.classList.contains('expanded');

        // Ëß¶ÂèëÊµÅÂÖâÊïàÊûú
        toggleIcon.classList.add('clicked');
        setTimeout(() => {
          toggleIcon.classList.remove('clicked');
