          const angle = Math.random() * Math.PI * 2;
          const radius = 10 + Math.random() * 16;
          const x = Math.cos(angle) * radius;
          const y = Math.sin(angle) * radius;
          const size = 1 + Math.random() * 2.6;
          const delay = (Math.random() * 2.5).toFixed(2) + 's';
          const twinkle = (2.4 + Math.random() * 2.4).toFixed(2) + 's';
          const drift = (6 + Math.random() * 4).toFixed(2) + 's';
          const scale = (0.8 + Math.random() * 0.6).toFixed(2);
