        let cssW = 0,
          cssH = 0; // FIX: ËÆ∞ÂΩï‰∏äÊ¨° CSS Â∞∫ÂØ∏ÔºåÈÅøÂÖçÊó†ÂèòÂåñÊó∂ÈáçÊéí

        function createStar() {
          const x = Math.random() * w;
          const y = Math.random() * h;
          const r = (Math.random() * 1.1 + 0.3) * dpr;
          const depth = Math.random();
          const baseAlpha = 0.45 + Math.random() * 0.45;
          const amp = 0.25 + Math.random() * 0.35;
          const tw = 0.0015 + Math.random() * 0.003;
          const driftX = (0.02 + depth * 0.1) * dpr;
          const driftY = (0.005 + depth * 0.03) * dpr;
          const phase = Math.random() * Math.PI * 2;
          return { x, y, r: r * (0.8 + depth * 0.6), baseAlpha, amp, tw, driftX, driftY, phase };
        }

        function targetCountFor(area) {
          return Math.min(220, Math.round(area / 2200));
        }

        function resize() {
          const rect = canvas.getBoundingClientRect();
          const nextDpr = Math.min(window.devicePixelRatio || 1, 2);
          const nextCssW = rect.width;
          const nextCssH = rect.height;

          // Ê≤°ÊúâÂÆûÈôÖ CSS Â∞∫ÂØ∏ÂèòÂåñÂàô‰∏çÈáçÊéíÔºàÂ±ïÂºÄÊó∂ÈÅøÂÖçÈó™ÁÉÅÔºâ
          if (Math.round(nextCssW) === Math.round(cssW) && Math.round(nextCssH) === Math.round(cssH)) {
            return;
          }

          const prevW = w;
          const prevH = h;
          const prevDpr = dpr;

          dpr = nextDpr;

          canvas.width = Math.max(1, Math.floor(nextCssW * dpr));
          canvas.height = Math.max(1, Math.floor(nextCssH * dpr));
          canvas.style.width = nextCssW + 'px';
          canvas.style.height = nextCssH + 'px';

          w = canvas.width;
          h = canvas.height;

          const area = Math.max(1, nextCssW * nextCssH);
          const target = targetCountFor(area);

          // FIX: ‰øùÁïôÂπ∂ÊåâÊØî‰æãÁº©ÊîæÁé∞ÊúâÊòüÊòüÔºåÈÅøÂÖç‚Äú‰π±Âä®‚Äù
          if (prevW > 0 && prevH > 0) {
            const scaleX = w / prevW;
            const scaleY = h / prevH;
            const dprRatio = dpr / (prevDpr || dpr);
            for (const s of stars) {
              s.x *= scaleX;
              s.y *= scaleY;
              s.r *= dprRatio;
              s.driftX *= dprRatio;
              s.driftY *= dprRatio;
            }
          }

          // Âπ≥ÊªëÂ¢ûÂáèÊï∞ÈáèÔºàÂè™Ë°•Â∑ÆÊàñË£ÅÂâ™Ôºå‰∏çÈáçÂª∫ÊâÄÊúâÔºâ
          if (stars.length < target) {
            const toAdd = target - stars.length;
            for (let i = 0; i < toAdd; i++) stars.push(createStar());
          } else if (stars.length > target) {
            stars.length = target;
          }

          cssW = nextCssW;
          cssH = nextCssH;
        }

        function step() {
          if (!animate) return;
          ctx.clearRect(0, 0, w, h);
          for (const s of stars) {
            s.phase += s.tw;
            s.x += s.driftX;
            s.y -= s.driftY * 0.2;
            if (s.x > w + 2) s.x -= w + 4;
            if (s.y < -2) s.y += h + 4;
            const alpha = Math.max(0, Math.min(1, s.baseAlpha + Math.sin(s.phase) * s.amp));
            ctx.globalAlpha = alpha;
            const g = ctx.createRadialGradient(s.x, s.y, 0, s.x, s.y, 2.8 * dpr);
            g.addColorStop(0, 'rgba(255,255,255,1)');
            g.addColorStop(0.35, 'rgba(236,228,255,0.9)');
            g.addColorStop(1, 'rgba(170,150,255,0)');
            ctx.fillStyle = g;
            ctx.beginPath();
            ctx.arc(s.x, s.y, 1.4 * dpr + s.r, 0, Math.PI * 2);
            ctx.fill();
          }
          ctx.globalAlpha = 1;
          rafId = requestAnimationFrame(step);
        }

        const media = window.matchMedia('(prefers-reduced-motion: reduce)');
        function updateMotion() {
          const shouldAnimate = !media.matches;
          if (shouldAnimate && !animate) {
            animate = true;
            cancelAnimationFrame(rafId);
            rafId = requestAnimationFrame(step);
          } else if (!shouldAnimate && animate) {
            animate = false;
            cancelAnimationFrame(rafId);
          }
        }

        // ÂàùÊ¨°Â∞∫ÂØ∏ËÆæÂÆö
        resize();
        updateMotion();
        media.addEventListener ? media.addEventListener('change', updateMotion) : media.addListener(updateMotion);

        // ‰ªÖÂú®Á™óÂè£Â∞∫ÂØ∏ÂèòÊõ¥Êó∂ÔºåÊâçÂÖ®Â±Ä resize
        window.addEventListener('resize', resize);

        if (animate) rafId = requestAnimationFrame(step);
        return { resize };
      }

      document.addEventListener('keydown', function (event) {
        if (event.key === 'Enter' || event.key === ' ') {
          const activeElement = document.activeElement;
          if (activeElement && activeElement.classList.contains('aether-header')) {
            event.preventDefault();
            toggleCollapsible();
          }
        }
      });

      document.querySelector('.aether-header').setAttribute('tabindex', '0');

      window.addEventListener('load', () => {
        initParticles();
        starApi = initStarCanvas();
        updateIframeHeight();
      });

      const contentInnerEl = document.querySelector('.aether-content-inner');
      const observer = new MutationObserver(() => {
        const collapsible = document.getElementById('aetherCollapsible');
        const content = document.getElementById('aetherContent');
        const collapseButton = content.querySelector('.aether-collapse');
        if (collapsible.classList.contains('expanded')) {
