          const currentData = getStoredData();
          delete currentData[tagName];
          saveStoredData(currentData);
          updateStorageDisplay(container);
          toastr.info('Ê†áËÆ∞ ' + tagName + ' ÁöÑÊï∞ÊçÆÂ∑≤Ê∏ÖÁ©∫ÔºÅ');
        }
