    background-repeat: no-repeat;
    background-size: contain;
    pointer-events: none;
    z-index: 1;
}

/* 输入框样式 */
.range-block-counter input {
    background-color: #111227;      
    border: 2px solid #32345e;
    color: #fbdbb3;    
    text-shadow: 1px 1px 4px rgba(223, 198, 139, 0.5);       
    border-radius: 10px;   
}

/* 输入框焦点 */
.range-block-counter input:focus {
    border-color: #dfc68b;
    box-shadow: 0px 0px 4px 2px rgba(223, 198, 139, 0.2);
    outline: none;
}

/* === 基础面板 === */
/* --- 标题字体颜色 --- */
h3, h4 {
    color: #fbdbb3;
}

/* --- 展开标题样式除世界书条目 --- */
.inline-drawer-header:not(.world_entry .inline-drawer-header) {
    color: #fbdbb3;                     
    letter-spacing: 1px;
    background-color: #1c1e47;
    box-shadow: inset 0px 0px 5px rgba(223, 198, 139, 0.2);
    border: 2px solid transparent;
    border-radius: 10px;
    padding-left: 10px;
    padding-right: 5px;
    transition: border-color 0.3s ease; 
}

/* 展开标题悬停除世界书条目 */
.inline-drawer-header:not(.world_entry .inline-drawer-header):hover {
    border: 2px solid #dfc68b;
}

/* --- 滑动按钮样式 --- */
.fa-toggle-on {
    opacity: 0.8 !important;
    color: #fbdbb3;           
}

/* 滑动按钮悬停 */
.fa-toggle-on:hover {
    filter: brightness(120%) !important;  
}

/* --- 菜单按钮 --- */
/* 增强菜单按钮阴影 */
.menu_button {    
    box-shadow: 0 1px 2px var(--black50a),
            inset 0 1px 0 rgba(255, 255, 255, 0.1) !important;
}

/* 改变按钮背景颜色，排除了特定类 */
.menu_button:not(.redWarningBG):not(.greenSuccessBG):not(.danger):not(#delete_button):not(.red_button):not(.popup-button-ok):not(.mes_reasoning_edit_done):not(.mes_edit_done):not(.mes_reasoning_edit_cancel):not(.mes_edit_cancel):not(#dialogue_popup_ok):not(#dialogue_del_mes_ok) {
    background-color: #101333;    
}

/* --- 展开标题的下拉图标 --- */
.custom-drawer-icon, .inline-drawer-icon {
    color: #fbdbb3; 
    filter: brightness(90%);
}

/* --- 文本编辑框 --- */
textarea {   
    border: 2px solid #292a3d; 
    border-radius: 5px; 
    transition: border-color 0.3s ease; 
}

/* --- 选择框 --- */
select {   
    border: 2px solid #292a3d; 
    border-radius: 5px; 
    background-color: #111227; 
    transition: border-color 0.3s ease; 
}

/* --- 文本块状框 --- */
.text_pole {
    background-color: #111227; 
    border: 2px solid #292a3d; 
    transition: border-color 0.3s ease; 
}

/* --- 焦点样式（适用于textarea、select和.text_pole） --- */
textarea:focus,
select:focus,
.text_pole:focus {
    border-color: #dfc68b; 
    box-shadow: 0px 0px 4px 2px rgba(223, 198, 139, 0.2);
    outline: none; 
}

/* --- 大标题样式 --- */
#user-settings-block h4,
.standoutHeader {
    color: #fbdbb3;                     
    letter-spacing: 1px;
    padding-left: 10px;
    background-image: none;
    background-color: #1c1e47; 
    box-shadow: inset 0px 0px 5px rgba(223, 198, 139, 0.2);        
}

/* 右侧放大按钮 */
h4.title_restorable .right_menu_button {
    filter: sepia(0.7) hue-rotate(315deg) saturate(1.5) brightness(0.9) contrast(0.95);
}

h4.title_restorable .right_menu_button:hover {
    filter: sepia(0.7) hue-rotate(315deg) saturate(1.5) brightness(0.7) contrast(1); 
}

/* --- 禁用样式 --- */
/* 滑动条禁用 */
input[type="range"]:disabled {
     filter: brightness(0.7) !important;
}

/* 复选框和可输入区域禁用 */
